// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./EventRegistry.sol";
import "./AgeRestrictionVerifier.sol";

contract EventTicket is ERC721, Ownable {
    struct TicketMetadata {
        uint256 eventId;
        uint256 price;
        bool attended;
        bool resold;
        uint256 originalPrice;
        bool requiresAgeVerification;
        uint256 requiredAge; // 0=18+, 1=21+, 2=25+
    }
    
    mapping(uint256 => TicketMetadata) public tickets;
    mapping(address => bool) public verifiers; // Event organizers
    uint256 public ticketCounter;
    
    // Trustless Escrow & Refund
    EventRegistry public registry;
    AgeRestrictionVerifier public ageVerifier;

    mapping(uint256 => uint256) public escrowBalance;
    mapping(uint256 => bool) public eventCancelled;
    mapping(uint256 => bool) public fundsWithdrawn;
    
    event TicketMinted(address indexed buyer, uint256 indexed eventId, uint256 tokenId);
    event TicketVerified(uint256 indexed tokenId, bool attended);
    event TicketResold(uint256 indexed tokenId, address indexed newOwner);
    event AgeRestrictionApplied(uint256 indexed tokenId, uint256 minAge);
    event FundsWithdrawn(uint256 indexed eventId, uint256 amount);
    event EventCancelled(uint256 indexed eventId);
    event RefundClaimed(uint256 indexed tokenId, address indexed claimant, uint256 amount);
    
    constructor(address _registry, address _ageVerifier) ERC721("BlinkTicket", "BLINK") Ownable(msg.sender) {
        registry = EventRegistry(_registry);
        ageVerifier = AgeRestrictionVerifier(_ageVerifier);
    }
    
    // TRUSTLESS PURCHASE
    function purchaseTicket(uint256 eventId) external payable {
        EventRegistry.Event memory evt = registry.getEvent(eventId);
        
        require(evt.active, "Event not active in registry");
        require(!eventCancelled[eventId], "Event cancelled");
        require(msg.value >= evt.price, "Insufficient payment");
        // Note: Capacity check ideally happens here, but requires calls to Registry or tracking here.
        // We assume unlimited or Registry tracking updates elsewhere for simpler demo.
        
        if (evt.requiresAgeVerification) {
            bool verified = ageVerifier.isVerifiedForEvent(msg.sender, eventId);
            require(verified, "Age verification required");
        }

        uint256 tokenId = ticketCounter++;
        _safeMint(msg.sender, tokenId);
        
        tickets[tokenId] = TicketMetadata({
            eventId: eventId,
            price: msg.value,
            attended: false,
            resold: false,
            originalPrice: msg.value,
            requiresAgeVerification: evt.requiresAgeVerification,
            requiredAge: uint256(evt.ageRestriction)
        });
        
        escrowBalance[eventId] += msg.value;
        
        emit TicketMinted(msg.sender, eventId, tokenId);
    }

    // ORGANIZER ACTIONS
    function cancelEvent(uint256 eventId) external {
        EventRegistry.Event memory evt = registry.getEvent(eventId);
        require(msg.sender == evt.organizer || msg.sender == owner(), "Not authorized");
        require(!fundsWithdrawn[eventId], "Funds already withdrawn");
        
        eventCancelled[eventId] = true;
        emit EventCancelled(eventId);
    }

    function withdrawFunds(uint256 eventId) external {
        EventRegistry.Event memory evt = registry.getEvent(eventId);
        require(msg.sender == evt.organizer, "Not organizer");
        require(!eventCancelled[eventId], "Event cancelled");
        require(!fundsWithdrawn[eventId], "Already withdrawn");
        
        // Simple check: assume 1 day after event date for withdrawal
        // require(block.timestamp > evt.date + 1 days, "Event not concluded");

        uint256 amount = escrowBalance[eventId];
        escrowBalance[eventId] = 0;
        fundsWithdrawn[eventId] = true;

        (bool sent, ) = payable(msg.sender).call{value: amount}("");
        require(sent, "Failed to send Ether");
        
        emit FundsWithdrawn(eventId, amount);
    }

    // USER ACTIONS
    function claimRefund(uint256 tokenId) external {
        TicketMetadata storage ticket = tickets[tokenId];
        require(ownerOf(tokenId) == msg.sender, "Not ticket owner");
        require(eventCancelled[ticket.eventId], "Event not cancelled");
        require(ticket.price > 0, "Already refunded or free");

        uint256 refundAmount = ticket.price;
        ticket.price = 0; // Prevent re-entrancy / double refund (simple marker)
        _burn(tokenId); // Burn the ticket

        (bool sent, ) = payable(msg.sender).call{value: refundAmount}("");
        require(sent, "Failed to send Refund");
        
        emit RefundClaimed(tokenId, msg.sender, refundAmount);
    }
    
    // BACKEND / LEGACY MINT (If needed, updated to deposit directly if value sent, or just free mint)
    function mintTicket(
        address to,
        uint256 eventId,
        uint256 price,
        bool requiresAgeVerification,
        uint256 requiredAge
    ) external onlyOwner returns (uint256) {
        uint256 tokenId = ticketCounter++;
        _safeMint(to, tokenId);
        tickets[tokenId] = TicketMetadata({
            eventId: eventId,
            price: price,
            attended: false,
            resold: false,
            originalPrice: price,
            requiresAgeVerification: requiresAgeVerification,
            requiredAge: requiredAge
        });
        
        emit TicketMinted(to, eventId, tokenId);
        return tokenId;
    }
    
    function verifyAttendance(uint256 tokenId, bool ageVerified) external {
        require(verifiers[msg.sender], "Not authorized");
        require(ownerOf(tokenId) != address(0), "Ticket not found");
        
        if (tickets[tokenId].requiresAgeVerification) {
            require(ageVerified, "Age verification required");
        }
        
        tickets[tokenId].attended = true;
        emit TicketVerified(tokenId, true);
    }
    
    function setVerifier(address verifier, bool status) external onlyOwner {
        verifiers[verifier] = status;
    }
    
    function getTicketMetadata(uint256 tokenId) external view returns (TicketMetadata memory) {
        return tickets[tokenId];
    }
}

contracts/EventTicketing.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract EventTicketing is ERC721, Ownable {
    uint256 private _tokenIds;
    uint256 private _eventIds;

    enum EventStatus { Active, Completed, Cancelled }

    struct EventDetails {
        string name;
        string description;
        string imageCID;
        uint256 startTime;
        uint256 endTime;
        string venueName;
        string streetAddress;
        string city;
        string state;
        string postalCode;
        string country;
        bool isOnline;
        uint256 ticketPrice;
        uint256 maxAttendees;
        bool isPrivate;
    }

    struct Event {
        uint256 eventId;
        address organizer;
        string name;
        string description;
        string imageCID;
        uint256 startTime;
        uint256 endTime;
        string venueName;
        string streetAddress;
        string city;
        string state;
        string postalCode;
        string country;
        bool isOnline;
        uint256 ticketPrice;
        uint256 maxAttendees;
        uint256 ticketsSold;
        EventStatus status;
        bool isPrivate;
    }

    // Event ID => Event details
    mapping(uint256 => Event) public events;
    // Event ID => Hash1 => bool (to track if user has already bought ticket)
    mapping(uint256 => mapping(bytes32 => bool)) public eventTickets;
    // Token ID => Event ID
    mapping(uint256 => uint256) public ticketEventMapping;
    // Hash1 => array of event IDs (for The Graph to index user's events)
    mapping(bytes32 => uint256[]) public userEvents;
    // Hash2 => Token ID
    mapping(bytes32 => uint256) public ticketHashes;

    event EventCreated(
        uint256 indexed eventId,
        address indexed organizer,
        string name,
        uint256 startTime,
        uint256 endTime,
        uint256 ticketPrice,
        uint256 maxAttendees
    );

    event TicketMinted(
        uint256 indexed eventId,
        uint256 indexed tokenId,
        bytes32 indexed hash2
    );

    event TicketVerified(
        uint256 indexed eventId,
        uint256 indexed tokenId,
        bytes32 indexed hash2
    );

    constructor() ERC721("EventTicket", "EVTIX") Ownable(msg.sender) {
        _tokenIds = 0;
        _eventIds = 0;
    }

    function createEvent(EventDetails calldata details) external returns (uint256) {
        require(details.startTime > block.timestamp, "Start time must be in the future");
        require(details.endTime > details.startTime, "End time must be after start time");
        require(details.maxAttendees > 0, "Max attendees must be greater than 0");

        unchecked {
            _eventIds += 1;
        }
        uint256 eventId = _eventIds;

        events[eventId] = Event({
            eventId: eventId,
            organizer: msg.sender,
            name: details.name,
            description: details.description,
            imageCID: details.imageCID,
            startTime: details.startTime,
            endTime: details.endTime,
            venueName: details.venueName,
            streetAddress: details.streetAddress,
            city: details.city,
            state: details.state,
            postalCode: details.postalCode,
            country: details.country,
            isOnline: details.isOnline,
            ticketPrice: details.ticketPrice,
            maxAttendees: details.maxAttendees,
            ticketsSold: 0,
            status: EventStatus.Active,
            isPrivate: details.isPrivate
        });

        emit EventCreated(
            eventId,
            msg.sender,
            details.name,
            details.startTime,
            details.endTime,
            details.ticketPrice,
            details.maxAttendees
        );

        return eventId;
    }

    function mintTicket(
        uint256 eventId,
        bytes32 hash1,
        bytes32 hash2
    ) external payable returns (uint256) {
        Event storage eventDetails = events[eventId];
        require(eventDetails.eventId != 0, "Event does not exist");
        require(eventDetails.status == EventStatus.Active, "Event is not active");
        require(block.timestamp < eventDetails.endTime, "Event has ended");
        require(eventDetails.ticketsSold < eventDetails.maxAttendees, "Event is sold out");
        require(msg.value == eventDetails.ticketPrice, "Incorrect ticket price");
        require(!eventTickets[eventId][hash1], "Ticket already purchased");

        unchecked {
            _tokenIds += 1;
        }
        uint256 tokenId = _tokenIds;

        _safeMint(eventDetails.organizer, tokenId);
        
        eventTickets[eventId][hash1] = true;
        ticketEventMapping[tokenId] = eventId;
        ticketHashes[hash2] = tokenId;
        userEvents[hash1].push(eventId);
        eventDetails.ticketsSold++;

        emit TicketMinted(eventId, tokenId, hash2);

        // Transfer payment to organizer
        payable(eventDetails.organizer).transfer(msg.value);

        return tokenId;
    }

    // Override _update to prevent transfers (except minting)
    function _update(address to, uint256 tokenId, address auth) internal virtual override returns (address) {
        address from = _ownerOf(tokenId);
        if (from != address(0)) {
            revert("Tickets are non-transferable");
        }
        return super._update(to, tokenId, auth);
    }

    // View functions for The Graph indexing
    function getEventsByHash1(bytes32 hash1) external view returns (uint256[] memory) {
        return userEvents[hash1];
    }

    function getTokenIdByHash2(bytes32 hash2) external view returns (uint256) {
        return ticketHashes[hash2];
    }
}

contracts/IssueTicket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title IssueTicket
 * @dev NFT-based ticket issuance system with privacy-preserving user identification
 */
contract IssueTicket is ERC721, Ownable, ReentrancyGuard {
    uint256 private _nextTokenId;

    // Mapping from Hash1 (user identity hash) to their ticket tokenIds
    mapping(bytes32 => uint256[]) private userTickets;
    
    // Mapping from Hash2 (user+event hash) to check if ticket exists
    mapping(bytes32 => bool) private ticketExists;
    
    // Mapping from tokenId to eventId
    mapping(uint256 => uint256) private ticketToEvent;

    // Mapping to track valid event IDs
    mapping(uint256 => bool) private validEvents;

    event TicketIssued(uint256 indexed tokenId, uint256 indexed eventId, bytes32 indexed hash1);
    event EventAdded(uint256 indexed eventId);
    event EventRemoved(uint256 indexed eventId);

    error InvalidEventId(uint256 eventId);
    error TicketAlreadyExists(bytes32 hash2);
    error InvalidHash();
    error EventNotActive(uint256 eventId);
    error TicketNotFound();

    constructor() ERC721("EventTicket", "TCKT") Ownable(msg.sender) {
        _nextTokenId = 1;
    }

    /**
     * @dev Adds a new event to the system
     * @param eventId ID of the event to add
     */
    function addEvent(uint256 eventId) external onlyOwner {
        require(eventId > 0, "Invalid event ID");
        require(!validEvents[eventId], "Event already exists");
        validEvents[eventId] = true;
        emit EventAdded(eventId);
    }

    /**
     * @dev Removes an event from the system
     * @param eventId ID of the event to remove
     */
    function removeEvent(uint256 eventId) external onlyOwner {
        require(validEvents[eventId], "Event does not exist");
        validEvents[eventId] = false;
        emit EventRemoved(eventId);
    }

    /**
     * @dev Issues a new ticket NFT
     * @param hash1 Hash of user's identity (aadhar+credit+cvv)
     * @param hash2 Hash of user's identity + eventId
     * @param eventId ID of the event
     * @return tokenId The ID of the minted NFT ticket
     */
    function issueTicket(
        bytes32 hash1, 
        bytes32 hash2, 
        uint256 eventId
    ) external nonReentrant returns (uint256) {
        // Input validation
        if (hash1 == bytes32(0) || hash2 == bytes32(0)) revert InvalidHash();
        if (!validEvents[eventId]) revert EventNotActive(eventId);
        if (ticketExists[hash2]) revert TicketAlreadyExists(hash2);
        
        uint256 tokenId = _nextTokenId++;
        _safeMint(msg.sender, tokenId);
        
        ticketExists[hash2] = true;
        userTickets[hash1].push(tokenId);
        ticketToEvent[tokenId] = eventId;
        
        emit TicketIssued(tokenId, eventId, hash1);
        
        return tokenId;
    }

    /**
     * @dev Gets all tickets owned by a user
     * @param hash1 Hash of user's identity
     * @return Array of token IDs owned by the user
     */
    function getTicketsByUser(bytes32 hash1) external view returns (uint256[] memory) {
        if (hash1 == bytes32(0)) revert InvalidHash();
        return userTickets[hash1];
    }

    /**
     * @dev Checks if a user has a ticket for an event
     * @param hash2 Hash of user's identity + eventId
     * @return bool indicating if the ticket exists
     */
    function hasTicket(bytes32 hash2) external view returns (bool) {
        if (hash2 == bytes32(0)) revert InvalidHash();
        return ticketExists[hash2];
    }

    /**
     * @dev Gets the event ID for a ticket
     * @param tokenId ID of the ticket
     * @return eventId
     */
    function getEventId(uint256 tokenId) external view returns (uint256) {
        if (_ownerOf(tokenId) == address(0)) revert TicketNotFound();
        return ticketToEvent[tokenId];
    }

    /**
     * @dev Checks if an event is valid and active
     * @param eventId ID of the event
     * @return bool indicating if the event is valid
     */
    function isEventValid(uint256 eventId) external view returns (bool) {
        return validEvents[eventId];
    }
}

contracts/MergedContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title MergedContract
 * @dev Combines event creation and ticket issuance with privacy-preserving features
 */
contract MergedContract is ERC721, Ownable, ReentrancyGuard {
    uint256 private _nextTokenId;
    uint256 private _nextEventId;

    enum EventStatus { Active, Completed, Cancelled }

    struct Event {
        uint256 eventId;
        address organizer;
        string name;
        string description;
        string imageCID;
        uint256 startTime;
        uint256 endTime;
        string venueName;
        string streetAddress;
        string city;
        string state;
        string postalCode;
        string country;
        bool isOnline;
        uint256 ticketPrice;
        uint256 maxAttendees;
        uint256 ticketsSold;
        EventStatus status;
        bool isPrivate;
    }

    // Event storage
    mapping(uint256 => Event) public events;
    mapping(address => uint256[]) public organizerEvents;

    // Ticket management
    mapping(uint256 => uint256) private ticketToEvent; // tokenId => eventId
    mapping(bytes32 => uint256[]) private userTickets; // hash1 => tokenIds
    mapping(bytes32 => bool) private ticketExists; // hash2 => exists
    mapping(uint256 => mapping(bytes32 => bool)) private eventTickets; // eventId => hash1 => has ticket
    mapping(bytes32 => uint256[]) public userEvents; // hash1 => eventIds
    mapping(bytes32 => uint256) public ticketHashes; // hash2 => tokenId

    // Events
    event EventCreated(
        uint256 indexed eventId,
        address indexed organizer,
        string name,
        uint256 startTime,
        uint256 endTime,
        uint256 ticketPrice,
        uint256 maxAttendees
    );
    event EventCancelled(uint256 indexed eventId);
    event EventCompleted(uint256 indexed eventId);
    event TicketIssued(uint256 indexed tokenId, uint256 indexed eventId, bytes32 indexed hash1);
    event TicketBurned(uint256 indexed tokenId, uint256 indexed eventId);
    event RefundIssued(address indexed to, uint256 indexed eventId, uint256 amount);

    // Custom errors
    error InvalidEventId(uint256 eventId);
    error EventNotActive(uint256 eventId);
    error TicketAlreadyExists(bytes32 hash2);
    error InvalidHash();
    error EventNotFound();
    error UnauthorizedAccess();
    error EventAlreadyEnded();
    error InsufficientPayment();
    error EventSoldOut();
    error RefundFailed();

    constructor() ERC721("EventTicket", "TCKT") Ownable(msg.sender) {
        _nextTokenId = 1;
        _nextEventId = 1;
    }

    /**
     * @dev Creates a new event
     */
    function createEvent(
        string memory name,
        string memory description,
        string memory imageCID,
        uint256 startTime,
        uint256 endTime,
        string memory venueName,
        string memory streetAddress,
        string memory city,
        string memory state,
        string memory postalCode,
        string memory country,
        bool isOnline,
        uint256 ticketPrice,
        uint256 maxAttendees,
        bool isPrivate
    ) external returns (uint256) {
        require(startTime > block.timestamp, "Start time must be in the future");
        require(endTime > startTime, "End time must be after start time");
        require(maxAttendees > 0, "Max attendees must be greater than 0");

        uint256 eventId = _nextEventId++;

        events[eventId] = Event({
            eventId: eventId,
            organizer: msg.sender,
            name: name,
            description: description,
            imageCID: imageCID,
            startTime: startTime,
            endTime: endTime,
            venueName: venueName,
            streetAddress: streetAddress,
            city: city,
            state: state,
            postalCode: postalCode,
            country: country,
            isOnline: isOnline,
            ticketPrice: ticketPrice,
            maxAttendees: maxAttendees,
            ticketsSold: 0,
            status: EventStatus.Active,
            isPrivate: isPrivate
        });

        organizerEvents[msg.sender].push(eventId);

        emit EventCreated(
            eventId,
            msg.sender,
            name,
            startTime,
            endTime,
            ticketPrice,
            maxAttendees
        );

        return eventId;
    }

    /**
     * @dev Issues a new ticket NFT with payment
     */
    function purchaseTicket(
        bytes32 hash1,
        bytes32 hash2,
        uint256 eventId
    ) external payable nonReentrant returns (uint256) {
        Event storage eventDetails = events[eventId];
        
        if (hash1 == bytes32(0) || hash2 == bytes32(0)) revert InvalidHash();
        if (eventDetails.eventId == 0) revert EventNotFound();
        if (eventDetails.status != EventStatus.Active) revert EventNotActive(eventId);
        if (block.timestamp >= eventDetails.endTime) revert EventAlreadyEnded();
        if (eventDetails.ticketsSold >= eventDetails.maxAttendees) revert EventSoldOut();
        if (msg.value != eventDetails.ticketPrice) revert InsufficientPayment();
        if (ticketExists[hash2]) revert TicketAlreadyExists(hash2);
        if (eventTickets[eventId][hash1]) revert TicketAlreadyExists(hash2);

        uint256 tokenId = _nextTokenId++;
        _safeMint(msg.sender, tokenId);

        // Update ticket mappings
        ticketExists[hash2] = true;
        eventTickets[eventId][hash1] = true;
        userTickets[hash1].push(tokenId);
        ticketToEvent[tokenId] = eventId;
        ticketHashes[hash2] = tokenId;
        userEvents[hash1].push(eventId);
        eventDetails.ticketsSold++;

        // Transfer payment to event organizer
        (bool sent, ) = payable(eventDetails.organizer).call{value: msg.value}("");
        require(sent, "Failed to send payment to organizer");

        emit TicketIssued(tokenId, eventId, hash1);
        return tokenId;
    }

    /**
     * @dev Cancels an event and enables refunds
     */
    function cancelEvent(uint256 eventId) external {
        Event storage eventDetails = events[eventId];
        if (eventDetails.eventId == 0) revert EventNotFound();
        if (msg.sender != eventDetails.organizer && msg.sender != owner()) revert UnauthorizedAccess();
        if (eventDetails.status != EventStatus.Active) revert EventNotActive(eventId);

        eventDetails.status = EventStatus.Cancelled;
        emit EventCancelled(eventId);
    }

    /**
     * @dev Burns a ticket and issues refund if event is cancelled
     */
    function burnTicket(uint256 tokenId) external nonReentrant {
        require(ownerOf(tokenId) == msg.sender || isApprovedForAll(ownerOf(tokenId), msg.sender), "Not token owner or approved");
        uint256 eventId = ticketToEvent[tokenId];
        Event storage eventDetails = events[eventId];
        
        if (eventDetails.eventId == 0) revert EventNotFound();
        
        // Only allow burning if event is cancelled or user initiated
        bool isCancelled = eventDetails.status == EventStatus.Cancelled;
        require(isCancelled || msg.sender == ownerOf(tokenId), "Cannot burn ticket");

        // Process refund if event was cancelled
        if (isCancelled) {
            (bool sent, ) = payable(ownerOf(tokenId)).call{value: eventDetails.ticketPrice}("");
            if (!sent) revert RefundFailed();
            emit RefundIssued(ownerOf(tokenId), eventId, eventDetails.ticketPrice);
        }

        // Burn the ticket
        _burn(tokenId);
        eventDetails.ticketsSold--;
        emit TicketBurned(tokenId, eventId);
    }

    /**
     * @dev Completes an event
     */
    function completeEvent(uint256 eventId) external {
        Event storage eventDetails = events[eventId];
        if (eventDetails.eventId == 0) revert EventNotFound();
        if (msg.sender != eventDetails.organizer && msg.sender != owner()) revert UnauthorizedAccess();
        if (eventDetails.status != EventStatus.Active) revert EventNotActive(eventId);
        if (block.timestamp < eventDetails.endTime) revert("Event not yet ended");

        eventDetails.status = EventStatus.Completed;
        emit EventCompleted(eventId);
    }

    // View functions
    function getEventDetails(uint256 eventId) external view returns (Event memory) {
        Event memory eventDetails = events[eventId];
        if (eventDetails.eventId == 0) revert EventNotFound();
        return eventDetails;
    }

    function getTicketsByUser(bytes32 hash1) external view returns (uint256[] memory) {
        if (hash1 == bytes32(0)) revert InvalidHash();
        return userTickets[hash1];
    }

    function getUserEvents(bytes32 hash1) external view returns (uint256[] memory) {
        if (hash1 == bytes32(0)) revert InvalidHash();
        return userEvents[hash1];
    }

    function verifyTicket(bytes32 hash2) external view returns (bool, uint256, uint256) {
        if (hash2 == bytes32(0)) revert InvalidHash();
        uint256 tokenId = ticketHashes[hash2];
        if (tokenId == 0) return (false, 0, 0);
        uint256 eventId = ticketToEvent[tokenId];
        return (true, tokenId, eventId);
    }

    // Override transfer functions to make tickets non-transferable
    function _update(address to, uint256 tokenId, address auth) internal virtual override returns (address) {
        address from = _ownerOf(tokenId);
        if (from != address(0)) {
            revert("Tickets are non-transferable");
        }
        return super._update(to, tokenId, auth);
    }
}

contracts/RoyaltyManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract RoyaltyManager {
    struct RoyaltySplit {
        address organizer;
        address platform;
        uint256 organizerShare;
        uint256 platformShare;
    }
    
    mapping(uint256 => RoyaltySplit) public royalties; // eventId => split
    
    function splitPayment(
        uint256 eventId,
        address organizer,
        uint256 amount,
        uint256 royaltyPercent
    ) external pure returns (uint256 organizerAmount, uint256 platformAmount) {
        platformAmount = (amount * 2) / 100; // 2% platform fee
        organizerAmount = amount - platformAmount;
    }
    
    function processResaleSplit(
        uint256 eventId,
        uint256 resalePrice,
        uint256 royaltyPercent
    ) external pure returns (uint256 organizerRoyalty, uint256 sellerProceeds) {
        organizerRoyalty = (resalePrice * royaltyPercent) / 100;
        sellerProceeds = resalePrice - organizerRoyalty;
    }
}

